mapper.select:
SELECT career_id AS careerId, class_id AS classId, teach_class_id AS teachClassId, teacher_name AS teacherName, course_name AS courseName, house_name AS houseName, nin_arrange.must AS must, weekly, nin_arrange.start_time AS startTime, nin_arrange.end_time AS endTime, week, pitch_num AS pitchNum, people_num AS peopleNum FROM nin_teacher, nin_course, nin_house, nin_arrange
nin_arrange.del_flag = 0 AND nin_teacher.id = nin_arrange.teacher_id AND nin_course.id = nin_arrange.course_id AND nin_house.id = nin_arrange.house_id
AND nin_arrange.teacher_id = #{teacherId}
AND (
nin_arrange.teach_class_id = #{item1}
OR
nin_arrange.class_id = #{item2}
)

SELECT nin_arrange.id AS id, career_name AS careerName, IFNULL((SELECT class_name FROM nin_class WHERE nin_class.id = nin_arrange.class_id), (SELECT GROUP_CONCAT(class_name SEPARATOR ';') FROM nin_teach_class, nin_class WHERE nin_arrange.teach_class_id = nin_teach_class.teach_class_id AND nin_teach_class.class_id = nin_class.id)) AS className, IFNULL(teacher_name, "暂无") AS teacherName, teacher_id AS teacherId, course_name AS courseName, IFNULL(house_name, "暂无") AS houseName, house_id AS houseId, nin_arrange.must AS must, weekly, nin_arrange.start_time AS startTime, nin_arrange.end_time AS endTime, week, pitch_num AS pitchNum FROM (((nin_arrange LEFT JOIN nin_teacher ON nin_arrange.teacher_id = nin_teacher.id) LEFT JOIN nin_house ON nin_arrange.house_id = nin_house.id) INNER JOIN nin_course ON nin_arrange.course_id = nin_course.id) INNER JOIN nin_career On nin_arrange.career_id = nin_career.id WHERE nin_arrange.del_flag = 0
AND(
nin_arrange.teach_class_id = #{item}
)
AND career_id = #{careerId}
AND week = #{week}
AND pitch_num = #{pitchNum}
AND class_id = #{classId}
AND teacher_name LIKE CONCAT('%',#{teacherName},'%')
AND house_name LIKE CONCAT('%',#{houseName},'%')
AND course_name LIKE CONCAT('%',#{courseName},'%')
ORDER BY nin_arrange.must, nin_arrange.career_id, nin_arrange.course_id, teach_class_id, week, pitch_num

SELECT *
FROM nin_arrange



            OR teacher_id = #{teacherId}


            OR house_id = #{houseId}
