mapper.select:
SELECT career_id AS careerId, class_id AS classId, teach_class_id AS teachClassId, teacher_name AS teacherName, course_name AS courseName, house_name AS houseName, nin_arrange.must AS must, weekly, nin_arrange.start_time AS startTime, nin_arrange.end_time AS endTime, week, pitch_num AS pitchNum, people_num AS peopleNum FROM nin_teacher, nin_course, nin_house, nin_arrange
nin_arrange.del_flag = 0 AND nin_teacher.id = nin_arrange.teacher_id AND nin_course.id = nin_arrange.course_id AND nin_house.id = nin_arrange.house_id
AND nin_arrange.teacher_id = #{teacherId}
AND(
nin_arrange.teach_class_id = #{item1}
nin_arrange.class_id = #{item2}
)
