mapper.select:
SELECT nin_career.college AS college, nin_class.id AS id, nin_class.career_id AS careerId, nin_career.career_name AS careerName, class_name AS className, people_num AS peopleNum, course_num AS courseNum FROM nin_class, nin_career WHERE nin_class.del_flag = 0 AND nin_class.career_id = nin_career.id
AND college = #{college}
AND career_id = #{careerId}
AND class_name LIKE CONCAT('%',#{className},'%')
ORDER BY career_id, class_name

SELECT nin_class.id AS classId, class_name AS className, career_id AS careerId FROM nin_class, nin_career WHERE nin_class.del_flag = 0 AND nin_class.career_id = nin_career.id
AND college = #{college}
AND career_id = #{careerId}
ORDER BY college, career_id, class_name

mapper.update:
UPDATE nin_class
SET people_num = people_num + 1
WHERE id = #{id}

UPDATE nin_class
SET people_num = people_num - 1
WHERE id = #{id}

UPDATE nin_class
SET people_num = #{peopleNum}
WHERE id = #{id}

UPDATE nin_class
SET course_num = CASE
career_id


        WHEN #{item.careerId} THEN course_num  + #{item.courseNum}

    END
    WHERE career_id IN
    (

        #{item.careerId}

    )
