mapper.update:
UPDATE nin_setting SET
    open_state = #{openState}

        ,open_time = #{openTime}


        ,close_time = #{closeTime}

WHERE id IN

    #{item}

mapper.select:
SELECT nin_setting.id, course_id, user_type, course_name, open_state, open_time, close_time
FROM nin_setting INNER JOIN nin_course ON nin_setting.course_id = nin_course.id
WHERE user_type = #{userType}

    AND open_state = #{openState}


    AND course_name LIKE CONCAT('%', #{courseName}, '%')
